import {useState, useEffect} from 'react';
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import {Quill, ReactQuillProps} from 'react-quill';
import { Outlet } from "react-router-dom"

function Layout() {
    const [isNotesListVisible, setIsNotesListVisible] = useState(true);
    function noteToggle() {
        setIsNotesListVisible(prevState => !prevState);
    };

    const [notes, setNotes] = useState([]);
    const navigate = useNavigate();

    function addNote() {
      const newNoteId = notes.length + 1;
      const newNote = { id: newNoteId, title: "Untitled", content: "..." };
      setNotes([...notes, newNote]);
      navigate(`/notes/${newNoteId}/edit`, { state: { noteId: newNoteId } });

    }

    const [noteTitle, setNoteTitle] = useState('Untitled');
    const handleTitleChange = (event) => {
      setNoteTitle(event.target.value);
    }




