        //   <div className='titles'>
        //     <h1 className='main-title'>Lotion</h1>
        //     <h4 className='minor-title'>Like Notion, but worse.</h4>
        //   </div>
        // </div>

        {/* <div className='main-body'>

          <div className='notes-list-cont' style={{ display: isNotesListVisible ? '' : 'none' }}>
            <div className='notes-head'>
              <h2>Notes</h2>
              <div className='plus-container' onClick={() => {addNote()}}>
                <i>&#43;</i>
              </div>
            </div>
            <div className='notes-list-body'>
              <h3 id='no-notes'>No Notes Yet</h3>
              <div className='notes-list'>
              </div>
            </div>
          </div>

          <div className='note-edit-area'>
            <div className='note-edit-head'>
            </div>
            <h2 id="default-body">Select a note, or create a new one.</h2>
          </div>

        </div> */}
        // </>
    return(
        <>
            <div id="head-area">
                <div className='menu-icon-cont' onClick={() => {noteToggle()}}>
                <i className='menu-icon'>&#9776;</i>
            </div>
            <div className='titles'>
                <h1 className='main-title'>Lotion</h1>
                <h4 className='minor-title'>Like Notion, but worse.</h4>
            </div>
            <hr></hr>
            <div className="page">
            {showSidebar && (
                <Sidebar
                notes={notes}
                onAddNote={onAddNote}
                activeNote={activeNote}
                setActiveNote={setActiveNote}
                />
            )}
            <Main
                activeNote={getActiveNote()}
                onDeleteNote={onDeleteNote}
                onUpdateNote={onUpdateNote}
            />
            </div>
        </div>
        </> 
    );
}

export default Layout;
