import {Link} from 'react-router-dom'
import { useEffect, useState } from "react";

function Sidebar({ notes, addNote, activeNote, setActiveNote }) {
    const [isNotesListVisible, setIsNotesListVisible] = useState(true);
    function noteToggle() {
        setIsNotesListVisible(prevState => !prevState);
    };


    // function addNote() {
    //     const newNoteId = notes.length + 1;
    //     const newNote = { id: newNoteId, title: "Untitled", content: "..." };
    //     setNotes([...notes, newNote]);
    //     navigate(`/notes/${newNoteId}/edit`, { state: { noteId: newNoteId } });

    //   }
//   const noNotesMessage = notes.length === 0 && <div id="no-note">No Notes Yet</div>;
   return (
    <div className='main-body'>
        <div className='notes-list-cont' style={{ display: isNotesListVisible ? '' : 'none' }}>
            <div className='notes-head'>
                <h2>Notes</h2>
                <div className='plus-container' onClick={() => {addNote()}}>
                <i>&#43;</i>
            </div>
            </div>
            <div className='notes-list-body'>
                { notes.length === 0 ? <h3 id='no-notes'>No Notes Yet</h3> : null }
                <div className='notes-list'>
                { notes.map(note => (
                    <Link to = {`/notes/${note.id}`}>
                        <div className={`note ${note.id === activeNote && "active"}`} key={note.id} onClick = {() => setActiveNote(note.id)}>
                            <h2>{note.title}</h2>
                            <small className="note-meta">
                                {new Date(note.dateCreated).toLocaleDateString("en-US", {
                                    month: "long",
                                    day: "numeric",
                                    year: "numeric",
                                    hour: "numeric",
                                    minute: "numeric",
                                    hour12: true,
                                })}
                            </small>
                            <p>{note.body && note.body.substr(0, 20).replace(/<\/?[^>]+(>|$)/g, "") + "..."}</p>
                        </div>
                    </Link>
                ))}
                </div>
            </div>
        </div>
    </div>
  );
}
export default Sidebar;
