import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

function Sidebar({ notes, addNote, activeNote, setActiveNote, setUserEditing, UserEditing }) {
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredNotes, setFilteredNotes] = useState(notes);
    const [allTags, setAllTags] = useState([]);

    // Get all unique tags across all notes
    useEffect(() => {
      let tags = new Set();
      notes.forEach(note => {
        note.tags.forEach(tag => tags.add(tag));
      });
      setAllTags(Array.from(tags));
    }, [notes]);

    // Filter notes by search term and selected tags
    useEffect(() => {
      const selectedTags = allTags.filter(tag => searchTerm.includes(tag));
      setFilteredNotes(notes.filter(note => {
        if (selectedTags.length === 0) return true;
        return note.tags.some(tag => selectedTags.includes(tag));
      }));
    }, [notes, searchTerm, allTags]);

    function handleTagInput(event) {
      setSearchTerm(event.target.value);
    }

    return (
      <div className='sidebar'>
        <div className='note-search'>
          <input type='text' placeholder='Search notes...' value={searchTerm} onChange={handleTagInput} />
        </div>
        <div className='note-list'>
          {filteredNotes.map(note => (
            <Link
              to={`/notes/${note.id}`}
              key={note.id}
              onClick={() => {
                setActiveNote(note);
                setUserEditing(false);
              }}
              className={`note-list-item${note.id === activeNote?.id ? ' active' : ''}`}
            >
              <h3>{note.title}</h3>
              <div className='note-tags'>
                {note.tags.map(tag => (
                  <span key={tag} className='note-tag'>{tag}</span>
                ))}
              </div>
            </Link>
          ))}
        </div>
        <div className='add-note'>
          <button onClick={addNote}>Add Note</button>
        </div>
      </div>
    );
  }


export default Sidebar;
