import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

function Sidebar({ notes, addNote, activeNote, setActiveNote, setUserEditing, UserEditing }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredNotes, setFilteredNotes] = useState(notes);
  const [tagSuggestions, setTagSuggestions] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);

  useEffect(() => {
    const filtered = notes.filter(note => {
      const noteTags = note.tags || [];
      const matchesSearchTerm = note.title.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesSelectedTags = selectedTags.every(tag => noteTags.includes(tag));
      return matchesSearchTerm && matchesSelectedTags;
    });
    setFilteredNotes(filtered);
  }, [notes, searchTerm, selectedTags]);

  useEffect(() => {
    const allTags = notes.reduce((tags, note) => {
      note.tags.forEach(tag => {
        if (!tags.includes(tag)) {
          tags.push(tag);
        }
      });
      return tags;
    }, []);
    const suggestedTags = allTags.filter(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));
    setTagSuggestions(suggestedTags);
  }, [notes, searchTerm]);

  const handleSearchChange = event => {
    setSearchTerm(event.target.value);
  };

  const handleTagClick = tag => {
    if (selectedTags.includes(tag)) {
      setSelectedTags(selectedTags.filter(t => t !== tag));
    } else {
      setSelectedTags([...selectedTags, tag]);
    }
  };

  const handleTagDelete = tag => {
    setSelectedTags(selectedTags.filter(t => t !== tag));
  };

  const handleNoteSort = () => {
    const sortedNotes = [...filteredNotes];
    sortedNotes.sort((noteA, noteB) => {
      const titleA = noteA.title.toLowerCase();
      const titleB = noteB.title.toLowerCase();
      if (titleA < titleB) {
        return -1;
      }
      if (titleA > titleB) {
        return 1;
      }
      return 0;
    });
    setFilteredNotes(sortedNotes);
  };

  return (
    <div className="notes-list-cont">
      <div className="notes-head">
        <h2>Notes</h2>
        <div className="plus-container" onClick={() => addNote()}>
          <i>&#43;</i>
        </div>
      </div>
      <div className="search-area">
        <i>&#128270;</i>
        <input type="text" placeholder="Search" value={searchTerm} onChange={handleSearchChange} />
        <div className="tag-suggestions">
          {tagSuggestions.map(tag => (
            <div className="tag-suggestion" key={tag} onClick={() => handleTagClick(tag)}>
              {selectedTags.includes(tag) ? (
                <span className="tag-selected">{tag}</span>
              ) : (
                <span className="tag-unselected">{tag}</span>
              )}
            </div>
          ))}
        </div>
        {selectedTags.length > 0 && (
          <div className="selected-tags">
            {selectedTags.map(tag => (
              <div className="selected-tag" key={tag}>
                {tag}
                <span className="delete-tag" onClick={() => handleTagDelete(tag)}>
                  &times;
                </span>
              </div>
            ))}
          </div>
        )}
      </div>
      <div className="notes-list-body">
      {notes.length === 0 && <h3 id="no-notes">No Notes Yet</h3>}
      {filteredNotes.length === 0 && notes.length !== 0 && <h3 id="no-notes">No Notes Found</h3>}
        <div className="notes-list">
          {filteredNotes.map((note, index) => (
            <Link to={`/notes/${index+1}`} key={note.id}>
              <div
                className={`note ${note.id === activeNote && 'active'}`}
                key={note.id}
                onClick={() => {
                  setActiveNote(note.id);
                  setUserEditing(false);
                }}
              >
                <h2>{note.title ? note.title.substr(0, 30) : 'Untitled'}</h2>
                <small className="note-date">
                  {note.dateCreated &&
                    new Date(note.dateCreated).toLocaleDateString('en-US', {
                      month: 'long',
                      day: 'numeric',
                      year: 'numeric',
                      hour: 'numeric',
                      minute: 'numeric',
                      hour12: true,
                    })}
                </small>
                <p>
                  {note.body
                    ? note.body.length < 0
                      ? '...'
                      : `${note.body.substr(0, 45).replace(/<\/?[^>]+(>|$)/g, '')}` + '...'
                    : '...'}
                </p>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Sidebar;
