import { useEffect, useState, useRef } from "react";
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function Main({ activeNote, confirmDelete, onUpdateNote }) {
  const [isNotesListVisible, setIsNotesListVisible] = useState(true);
  const [editedNote, setEditedNote] = useState(activeNote);
  const [title, setTitle] = useState(''); // Use activeNote's title if it exists
//   const [editedTitle, setEditedTitle] = useState(title);  
  const[userEditing,setUserEditing]=useState(true);

//   useEffect(() => {
//     setEditedNote(activeNote);
//     setTitle(activeNote?.title || 'Untitled');
//     setEditedTitle(activeNote?.title || 'Untitled');
//   }, [activeNote]);

  const quillref = useRef(null);
  const toolbar = document.querySelector('.ql-toolbar');

  useEffect(()=>{
    if(activeNote){
        setTitle(activeNote.title);
    }
  },[activeNote]);

  const onSave = () => {
    const html = quillref.current.getEditor().container.root.innerHTML;
    onUpdateNote ({
     ...activeNote,
     body:html,
     dateCreated:Date.now(),
    });
    };

    const handleTitleChange = ()=> {
        onUpdateNote ({
            ...activeNote,
            title,
            dateCreated:Date.now(),
           });
      }

      const editMode = () => {
        setUserEditing(!userEditing);
        if(userEditing){
            onSave();
        }
      }




  const options = {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "numeric",
    second: "numeric",
  };

  const formatDate = (when) => {
    const formatted = new Date(when).toLocaleString("en-US", options);
    if (formatted === "Invalid Date") {
      return "";
    }
    return formatted;
  };

  if (!activeNote) {
    return <div id="default-body"><p>Select a note, or create a new one.</p></div>;
  }

  const onEdit = (key, value) => {
    onUpdateNote({
      ...activeNote,
      [key]: value,
      dateCreated:Date.now(),
    });
  };





  return (
    <div className='note-edit-area' style={{width: isNotesListVisible ? '' : '100vw' }}>
      <div className='note-edit-head'>
        <div className="input-container">
          <input
            type="text"
            id="title"

            autoFocus
            placeholder="Untitled"
            value={title} // Use edited title here
            onChange={(e) => setTitle(e.target.value)} // Update edited title when input value changes
            onBlur ={handleTitleChange}
          />
          <input
            type="datetime-local"
            defaultValue={new Date(Date.now() - new Date().getTimezoneOffset() * 60000).toISOString().slice(0, -8)}
            onChange={formatDate()}
          />
        </div>
        <div className="save-delete">
          <div className="save-btn" onClick={onSave}>
            <h3 id="save" onClick={editMode}>{userEditing ?'Save':'Edit'}</h3>
          </div>
          <div className="delete-btn">
            <h3 id="delete" onClick={() => confirmDelete(activeNote.id)}>Delete</h3>
          </div>
        </div>
      </div>
      <div className="note-edit-body">{userEditing? (
            <ReactQuill
            ref={quillref}
            theme="snow"
            value={activeNote.body} 
            placeholder='Your Note Here'
            // onChange={(value) => onEdit("body", value)}
            modules={{
                toolbar: [
                [{ 'header': [1, 2, 3, 4, false] }],
                ['bold', 'italic', 'underline', 'strike', 'link'],
                [{ 'color': [] }],
                [{ 'list': 'ordered'}, { 'list': 'bullet' }],
                ['clean']
                ],
            }}
            formats={[    "header",    "bold",    "italic",    "underline",    "strike",    "link", "color",  "list",    "bullet",    "clean",  ]}
            style={{ fontSize: "25px", width: isNotesListVisible ? "76vw" : "inherit"}}

        />
      ):(
        <p className="save-text">{activeNote.body && activeNote.body.replace(/<[^>]+>/g, '')}</p>
      )}

            </div>
        </div>

 );
}


export default Main;
