        <div className='note-edit-head'>
            <div className="input-container">
            <input type="text" autoFocus placeholder="Untitled" value={title} onChange={handleTitleChange} />
            <input type="datetime-local" value={new Date(Date.now() - new Date().getTimezoneOffset() * 60000).toISOString().slice(0, -8)} onChange={formatDate()}/>
            </div>
            <div className="save-delete">
            <div className="save-btn" onClick={handleSave}>
                <h3 id="save">Save</h3>
            </div>
            <div className="delete-btn">
                <h3 id="delete" onClick={confirmDelete}>Delete</h3>
            </div>
            </div>
        </div>
        <div className="note-edit-body">
            <ReactQuill
                theme="snow"
                value={activeNote.body} 
                placeholder='Your Note Here'
                onChange={(value) => onEdit("body", value)}
                modules={{
                    toolbar: [
                    [{ 'header': [1, 2, 3, 4, false] }],
                    ['bold', 'italic', 'underline', 'strike', 'link'],
                    [{ 'color': [] }],
                    [{ 'list': 'ordered'}, { 'list': 'bullet' }],
                    ['clean']
                    ],
                }}
                formats={[    "header",    "bold",    "italic",    "underline",    "strike",    "link", "color",  "list",    "bullet",    "clean",  ]}
                style={{ fontSize: "25px", maxWidth: isNotesListVisible ? "76vw" : "100vw"}}
            />
        </div>
    </div>

 );
}


export default Main;
